import React, { Component } from "react";
import axios from 'axios';

export default class Dashboard extends Component {


    render() {
        return (
            <form>
                <h3>Sign In</h3>

                <div className="form-group">
                    <label>Email address</label>
                    <input type="email" className="form-control" placeholder="Enter email" name='email'  value={this.state.email} onChange={e=>this.handleChange(e)}/>
                </div>

                <div className="form-group">
                    <label>Password</label>
                    <input type="password" className="form-control" placeholder="Enter password" name='password' value={this.state.password} onChange={e=>this.handleChange(e)}/>
                </div>

                <button type="button" className="btn btn-primary btn-block" onClick = {this.submit} >Submit</button>

            </form>
        );
    }

    submit = e => {
    const payload = {
    email: this.state.email,
    password: this.state.password
    }
    axios.post('http://localhost:3001/login', payload)
      .then((response) => {
        console.log(response.data)
        window.open('/dashboard', "home") //to open new page
      }, (error) => {
        console.log(error);
      });

    } 


}
